import { Component } from "react/cjs/react.production.min";

class Book extends Component{

    render(){
        let cover 
        if(this.props.book.imageLinks){
            cover=this.props.book.imageLinks.thumbnail
        }else
        return(
            <div className="book">
                <div className="book-top">
                    <div className="book-cover" style={{width:128,height:193,backgroundImage:`url("")`}}></div>
                    <div className="book-shelf-changer">
                        <select onChange={(o)=>this.props.shelfChange(this.props.book,o.target.value)} value={this.props.currentShelf}>
                        <option value="move" disabled>Move to...</option>
                                <option value="currentlyReading">Currently Reading</option>
                                <option value="wantToRead">Want to Read</option>
                                <option value="read">Read</option>
                                <option value="none">None</option>
                        </select>
                    </div>
                </div>
                <div className="book-title">{this.props.book.title}</div>
                <div className="book-authors">{this.props.book.authors}</div>


            </div>


        )
    }
}
export default Book;
