import { Component } from "react/cjs/react.production.min";
import Search from "./Components/Search";
import BookList from './Components/BookList';

import "./App.css";
import * as BooksAPI from './BooksAPI'
import {BrowserRouter, Route, Routes} from 'react-router-dom'


class App extends Component{

  state={
    books:[]
  }
  componentDidMount(){
    BooksAPI.getAll().then((books) => {this.setState({books: books})})
  }
  shelfChange=(book, newshelf)=>{
    BooksAPI.update(book,newShelf).then((result) =>{
      console.log('Update response', result)
      // Change the shelf property of the book object to new Shelf.
      book.shelf = newShelf
      //  Get the previous state book array without current book & add it to the new updated books array
      var updatedBooks = this.state.books.filter((resultBook) =>resultBook.id !== book.id)
      updatedBooks.push(book)
      // Set the new state with Updated Books array
      this.setState({books: updatedBooks})
  })

  }

  render(){
    return(
      <div className="app">
        <BrowserRouter>
        <Routes>
        <Route exact path="/" element={<BookList  books={this.state.books}
            changeShelf={this.changeShelf}/>}
        />
      <Route exact path="/search" element={ <Search shelfChange={this.shelfChange} books={this.state.books}></Search> }/>
        </Routes>
      </BrowserRouter>

      </div>
    )
  }
}

export default App;
